mapper.select:
SELECT 
    FROM private_message
WHERE conv_seq > #{last_seq}
AND visible_to_oneself = true
AND uid_a = #{uid_a}
AND uid_b = #{uid_b}
ORDER BY id ASC

SELECT 
    FROM private_message
WHERE visible_to_oneself = true
AND uid_a = #{uid_a}
AND uid_b = #{uid_b}
AND status = #{message_status}

SELECT 
FROM private_message
WHERE visible_to_oneself = true
AND uid_a = #{uid_a}
AND uid_b = #{uid_b}
AND id = #{id}

SELECT 
    FROM private_message
WHERE visible_to_oneself = true
AND uid_a = #{uid_a}
AND uid_b = #{uid_b}
